import Card from '../UI/Card';
import ExpenseItem from '../Expenses/ExpenseItem'
import './Expenses.css'
import ExpensesFilter from './ExpenseFilter';
import { useState } from 'react';
const Expenses=(props)=>
{ 
  const [selectedValue,setSelectedValue]=useState('2020');
    const FilterExpensesHandler = (filteredYear)=>{
       setSelectedValue(filteredYear);
       //props.FilterExpenses(filteredYear);
    }
    return (
      <div>              
        <Card className='expenses'> 
        <ExpensesFilter selectedYear = {selectedValue} FilterExpenses={FilterExpensesHandler}></ExpensesFilter>  
        {}     
        <ExpenseItem expTitle={props.expenses[0].expTitle} expAmount = {props.expenses[0].expAmount} expDate = {props.expenses[0].expDate}></ExpenseItem>
        <ExpenseItem expTitle={props.expenses[1].expTitle} expAmount = {props.expenses[1].expAmount} expDate = {props.expenses[1].expDate}></ExpenseItem>
        <ExpenseItem expTitle={props.expenses[2].expTitle} expAmount = {props.expenses[2].expAmount} expDate = {props.expenses[2].expDate}></ExpenseItem>      
      </Card>
      </div>
    );
}
export default Expenses;
